            position: relative;
            overflow: hidden;
        }

        .card-header::before {
            content: '♥';
            position: absolute;
            font-size: 80px;
            color: rgba(255, 255, 255, 0.1);
            top: -20px;
            left: -10px;
            transform: rotate(-15deg);
        }

        .card-header::after {
            content: '♥';
            position: absolute;
            font-size: 60px;
            color: rgba(255, 255, 255, 0.1);
            bottom: -15px;
            right: -5px;
            transform: rotate(15deg);
        }

        .card-header h2 {
            color: white;
            font-size: 26px;
            font-weight: bold;
            text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.2);
            position: relative;
            z-index: 1;
        }

        .card-body {
            padding: 24px;
        }

        .section {
            margin-bottom: 20px;
            background: white;
            border-radius: 16px;
            overflow: hidden;
            box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05);
            border: 2px solid var(--border-color);
        }

        .section-header {
            background: linear-gradient(135deg, var(--accent-purple) 0%, var(--soft-blue) 100%);
            padding: 14px 18px;
            cursor: pointer;
            display: flex;
            justify-content: space-between;
            align-items: center;
            color: white;
