        .section-content {
            max-height: 0;
            overflow: hidden;
            transition: max-height 0.4s ease;
            background: white;
        }

        .section-content.expanded {
            max-height: 2000px;
        }

        .property {
            padding: 16px 18px;
            border-bottom: 1px dashed var(--border-color);
        }

        .property:last-child {
            border-bottom: none;
        }

        .property-name {
            font-weight: bold;
            color: var(--text-dark);
            margin-bottom: 10px;
            font-size: 15px;
            display: flex;
            align-items: center;
        }

        .property-name::before {
            content: '◆';
