            transition: width 0.6s ease;
            border-radius: 10px;
            box-shadow: 0 0 10px rgba(255, 179, 217, 0.5);
        }

        .text-content {
            color: var(--text-dark);
            font-size: 14px;
            line-height: 1.8;
            padding: 4px 0;
