        }

        .world-info-item {
            display: flex;
            flex-direction: column;
            align-items: center;
            flex: 1;
        }

        .world-info-label {
            font-size: 12px;
            color: var(--text-light);
            margin-bottom: 4px;
        }

        .world-info-value {
            font-size: 14px;
            font-weight: bold;
            color: var(--text-dark);
        }

        @media (max-width: 480px) {
            .card-header h2 {
                font-size: 22px;
            }
            .section-header {
                font-size: 14px;
