            }
        }
    </style>
</head>
<body>
<div class="status-card" id="status-card">
    <div class="card-header">
        <h2>♡ ひなたの状態 ♡</h2>
    </div>

    <div class="card-body">
        <div class="world-info">
            <div class="world-info-item">
                <div class="world-info-label">时间</div>
                <div class="world-info-value" id="world-time">--</div>
            </div>
            <div class="world-info-item">
                <div class="world-info-label">地点</div>
                <div class="world-info-value" id="world-location">--</div>
            </div>
        </div>

        <div class="section">
            <div class="section-header expanded-header" onclick="toggleSection(this)">
                <span>橘日向 核心状态</span>
                <span>▲</span>
            </div>
            <div class="section-content expanded">
                <div class="property">
                    <div class="property-name">好感度</div>
                    <div class="property-value-container">
                        <span class="value-main" id="hinata-affection">0</span>
                        <span class="value-description" id="hinata-affection-desc"></span>
                    </div>
                    <div class="progress-bar-container">
                        <div id="hinata-affection-bar" class="progress-bar-value" style="width: 0%;"></div>
                    </div>
                </div>
            </div>
        </div>

        <div class="section">
            <div class="section-header expanded-header" onclick="toggleSection(this)">
                <span>外观与状态</span>
                <span>▲</span>
            </div>
            <div class="section-content expanded">
                <div class="property">
                    <div class="property-name">当前着装</div>
                    <div class="text-content" id="hinata-outfit">--</div>
                </div>
                <div class="property">
                    <div class="property-name">当前姿势</div>
                    <div class="text-content" id="hinata-pose">--</div>
                </div>
            </div>
        </div>

        <div class="section">
            <div class="section-header expanded-header" onclick="toggleSection(this)">
                <span>内心想法</span>
                <span>▲</span>
            </div>
            <div class="section-content expanded">
                <div class="property">
                    <div class="property-name">当前所想</div>
                    <div class="text-content" id="hinata-thoughts">--</div>
                </div>
            </div>
        </div>
    </div>
</div>

<script>
    function SafeGetValue(obj, path, defaultValue = "N/A") {
        let keys = Array.isArray(path) ? path : path.split('.');
        let current = obj;
        for (let i = 0; i < keys.length; i++) {
            if (current === undefined || current === null || typeof current !== 'object' || !current.hasOwnProperty(keys[i])) {
                return defaultValue;
            }
            current = current[keys[i]];
        }
        if (current === undefined || current === null) {
            return defaultValue;
        }
        if (Array.isArray(current)) {
            if (current.length > 0) {
                 const actualValue = current[0];
                 if (typeof actualValue === 'boolean') {
                     return actualValue;
                 }
                 return String(actualValue);
            } else {
                 return defaultValue;
            }
        }
        if (typeof current === 'boolean') {
            return current;
        }
        return String(current);
    }

    function updateProgressBar(barId, valueMainId, valueDescId, rawValueFromCharacterData, min, max, color) {
        const progressBar = document.getElementById(barId);
        const valueMainDisplay = document.getElementById(valueMainId);
        const valueDescDisplay = document.getElementById(valueDescId);
        if (!valueMainDisplay) return;
        let fullDisplayString = SafeGetValue({ temp: rawValueFromCharacterData }, 'temp', String(min));
        let numericValue = min;
        let descriptionText = "";
        const descriptionMatch = String(fullDisplayString).match(/\((.+)\)$/);
        if (descriptionMatch && descriptionMatch[1]) {
            descriptionText = descriptionMatch[1];
            fullDisplayString = String(fullDisplayString).substring(0, descriptionMatch.index).trim();
        }
        const valueMatch = String(fullDisplayString).match(/(-?\d+(\.\d+)?)(?=[^\d.]*$|->)/);
        if (valueMatch) {
            numericValue = parseFloat(valueMatch[1]);
            const parts = String(fullDisplayString).split('->');
            valueMainDisplay.innerText = parts.length > 1 ? parts[1].trim() : parts[0].trim();
        } else if (!isNaN(parseFloat(fullDisplayString))) {
            numericValue = parseFloat(fullDisplayString);
            valueMainDisplay.innerText = fullDisplayString;
        } else {
            if (Array.isArray(rawValueFromCharacterData) && typeof rawValueFromCharacterData[0] === 'number') {
                numericValue = rawValueFromCharacterData[0];
                valueMainDisplay.innerText = String(numericValue);
            } else if (typeof rawValueFromCharacterData === 'number' || typeof rawValueFromCharacterData === 'string') {
                valueMainDisplay.innerText = String(rawValueFromCharacterData);
            } else {
                valueMainDisplay.innerText = SafeGetValue({ temp: rawValueFromCharacterData }, 'temp', String(min));
            }
        }
        if (valueDescDisplay) {
            valueDescDisplay.innerText = descriptionText ? `(${descriptionText})` : "";
        }
        if (progressBar) {
            numericValue = Math.max(min, Math.min(max, numericValue));
            const range = max - min;
            const percentage = range === 0 ? (numericValue >= max ? 100 : 0) : ((numericValue - min) / range) * 100;
            progressBar.style.width = `${Math.max(0, Math.min(100, percentage))}%`;
            progressBar.style.backgroundColor = color ? color : 'var(--accent-highlight)';
        }
    }

    async function initDisplay() {
        try {
            const messages = await getChatMessages(getCurrentMessageId());
            if (!messages || messages.length === 0 || !messages[0].data) {
                document.getElementById('status-card').innerHTML = "<p style='color:red; padding:10px;'>无法加载状态数据 (messages empty)</p>";
                return;
            }
            const gameData = messages[0].data;
            const characterData = gameData.stat_data;
            if (!characterData) {
                document.getElementById('status-card').innerHTML = "<p style='color:red; padding:10px;'>无法加载状态数据 (characterData missing)</p>";
                return;
            }

            const worldTimeElement = document.getElementById('world-time');
            const worldLocationElement = document.getElementById('world-location');
            const hinataOutfitElement = document.getElementById('hinata-outfit');
            const hinataPoseElement = document.getElementById('hinata-pose');
            const hinataThoughtsElement = document.getElementById('hinata-thoughts');

            if (characterData.hasOwnProperty('世界')) {
                const worldTime = SafeGetValue(characterData, '世界.当前时间', '--');
                const worldLocation = SafeGetValue(characterData, '世界.当前地点', '--');

                if (worldTimeElement) worldTimeElement.innerText = worldTime;
                if (worldLocationElement) worldLocationElement.innerText = worldLocation;
            }

            if (characterData.hasOwnProperty('橘日向')) {
                updateProgressBar(
                    'hinata-affection-bar',
                    'hinata-affection',
                    'hinata-affection-desc',
                    SafeGetValue(characterData, '橘日向.好感度', 0),
                    0,
                    100,
                    'linear-gradient(90deg, var(--primary-pink) 0%, var(--secondary-pink) 100%)'
                );

                const outfit = SafeGetValue(characterData, '橘日向.当前着装', '--');
                const pose = SafeGetValue(characterData, '橘日向.当前姿势', '--');
                const thoughts = SafeGetValue(characterData, '橘日向.当前想法', '--');

                if (hinataOutfitElement) hinataOutfitElement.innerText = outfit;
                if (hinataPoseElement) hinataPoseElement.innerText = pose;
                if (hinataThoughtsElement) hinataThoughtsElement.innerText = thoughts;
            }
        } catch (error) {
            document.getElementById('status-card').innerHTML = `<p style='color:red; padding:10px;'>状态栏加载出错: ${error.message}</p>`;
        }
    }

    function toggleSection(element) {
        const content = element.nextElementSibling;
        const arrow = element.querySelector('span:last-child');
        if (!content || !arrow) return;
        const isExpanded = content.classList.toggle('expanded');
        element.classList.toggle('expanded-header', isExpanded);
        arrow.innerText = isExpanded ? '▲' : '▼';
    }

    document.addEventListener('DOMContentLoaded', initDisplay);
</script>
</body>
</html>
