<body>
    <div class="container">
        <div class="tabs" id="tabsContainer"></div>
        <div id="tabContents"></div>
    </div>

    <script>
        let currentData = null;

        function SafeGetValue(data, path, fallback) {
            try {
                if (!data) {
                    return fallback;
                }
                const keys = path.split('.');
                let value = data;
                for (const key of keys) {
                    if (value && typeof value === 'object' && key in value) {
                        value = value[key];
                    } else {
                        return fallback;
                    }
                }
                return value !== undefined && value !== null ? value : fallback;
            } catch (error) {
                console.error('SafeGetValue error:', error);
                return fallback;
            }
        }

        function updateProgressBar(elementId, value, max) {
            const bar = document.getElementById(elementId);
            if (bar) {
                const percentage = Math.min(100, Math.max(0, (value / max) * 100));
                bar.style.width = percentage + '%';
            }
        }

        function switchTab(tabName) {
            const tabs = document.querySelectorAll('.tab');
            const contents = document.querySelectorAll('.tab-content');

            tabs.forEach(tab => {
                if (tab.dataset.tab === tabName) {
                    tab.classList.add('active');
                } else {
                    tab.classList.remove('active');
                }
            });

            contents.forEach(content => {
                if (content.dataset.tab === tabName) {
                    content.classList.add('active');
                } else {
                    content.classList.remove('active');
                }
            });
        }

        function createWorldTab() {
            return `
                <div class="tab-content active" data-tab="world">
                    <div class="world-info">
                        <div class="world-card">
                            <div class="world-card-icon">🕐</div>
                            <div class="world-card-content">
                                <div class="world-card-label">时间</div>
                                <div class="world-card-value" id="world-time"></div>
                            </div>
                        </div>
                        <div class="world-card">
                            <div class="world-card-icon">📍</div>
                            <div class="world-card-content">
                                <div class="world-card-label">地点</div>
                                <div class="world-card-value" id="world-location"></div>
                            </div>
                        </div>
                    </div>
                </div>
            `;
        }

        function createCharacterTab(name, nameEn) {
            return `
                <div class="tab-content" data-tab="${nameEn}">
                    <div class="info-card">
                        <div class="info-row">
                            <span class="info-label">好感度</span>
                            <div class="progress-container">
                                <div class="progress-bar" id="${nameEn}-favorability"></div>
                                <div class="progress-text" id="${nameEn}-favorability-text">0/100</div>
                            </div>
                        </div>
                        <div class="info-row">
                            <span class="info-label">当前服饰</span>
                            <span class="info-value" id="${nameEn}-outfit"></span>
                        </div>
                        <div class="info-row">
                            <span class="info-label">当前姿势</span>
                            <span class="info-value" id="${nameEn}-pose"></span>
                        </div>
                    </div>
                    <div class="thought-box">
                        <div class="thought-text" id="${nameEn}-thought"></div>
                    </div>
                </div>
            `;
        }

        function initializeTabs(data) {
            const characters = [
                { name: '艾莉娅', nameEn: 'ellia' },
                { name: '莉莉安', nameEn: 'lilian' },
                { name: '维奥莱特', nameEn: 'violet' }
            ];

            const tabsContainer = document.getElementById('tabsContainer');
            const tabContents = document.getElementById('tabContents');

            tabsContainer.innerHTML = '<button class="tab active" data-tab="world" onclick="switchTab(\'world\')">世界</button>';
            tabContents.innerHTML = createWorldTab();

            characters.forEach(char => {
                const isInTeam = SafeGetValue(data, `${char.name}.是否入队`, false);
                if (isInTeam) {
                    tabsContainer.innerHTML += `<button class="tab" data-tab="${char.nameEn}" onclick="switchTab('${char.nameEn}')">${char.name}</button>`;
                    tabContents.innerHTML += createCharacterTab(char.name, char.nameEn);
                }
            });
        }

        function updateDisplay(data) {
            const worldTime = SafeGetValue(data, '世界.时间', '未知');
            const worldLocation = SafeGetValue(data, '世界.地点', '未知');

            const timeElement = document.getElementById('world-time');
            const locationElement = document.getElementById('world-location');

            if (timeElement) timeElement.textContent = worldTime;
            if (locationElement) locationElement.textContent = worldLocation;

            const characters = [
                { name: '艾莉娅', nameEn: 'ellia' },
                { name: '莉莉安', nameEn: 'lilian' },
                { name: '维奥莱特', nameEn: 'violet' }
            ];

            characters.forEach(char => {
                const isInTeam = SafeGetValue(data, `${char.name}.是否入队`, false);
                if (isInTeam) {
                    const favorability = SafeGetValue(data, `${char.name}.好感度`, 0);
                    const outfit = SafeGetValue(data, `${char.name}.当前服饰`, '未知');
                    const pose = SafeGetValue(data, `${char.name}.当前姿势`, '未知');
                    const thought = SafeGetValue(data, `${char.name}.当前想法`, '暂无想法');

                    updateProgressBar(`${char.nameEn}-favorability`, favorability, 100);

                    const favorabilityText = document.getElementById(`${char.nameEn}-favorability-text`);
                    const outfitElement = document.getElementById(`${char.nameEn}-outfit`);
                    const poseElement = document.getElementById(`${char.nameEn}-pose`);
                    const thoughtElement = document.getElementById(`${char.nameEn}-thought`);

                    if (favorabilityText) favorabilityText.textContent = `${favorability}/100`;
                    if (outfitElement) outfitElement.textContent = outfit;
                    if (poseElement) poseElement.textContent = pose;
                    if (thoughtElement) thoughtElement.textContent = thought;
                }
            });
        }

        async function initDisplay() {
            try {
                const messages = await getChatMessages(getCurrentMessageId());
                const gameData = messages[0].data;
                currentData = gameData.stat_data;

                initializeTabs(currentData);
                updateDisplay(currentData);
            } catch (error) {
                console.error('Failed to initialize display:', error);
                // 如果获取失败，使用默认数据
                currentData = {
                    "世界": {
                        "时间": "未知",
                        "地点": "未知"
                    },
                    "艾莉娅": {
                        "好感度": 0,
                        "是否入队": true,
                        "当前服饰": "未知",
                        "当前姿势": "未知",
                        "当前想法": "暂无想法"
                    }
                };
                initializeTabs(currentData);
                updateDisplay(currentData);
            }
        }

        if (document.readyState === 'loading') {
            document.addEventListener('DOMContentLoaded', initDisplay);
        } else {
            initDisplay();
        }

        if (typeof window.updateStatus === 'undefined') {
            window.updateStatus = async function() {
                await initDisplay();
            };
        }
    </script>
</body>
</html>
