<script>
    function SafeGetValue(obj, path, defaultValue = "N/A") {
        const keys = Array.isArray(path) ? path : path.split('.');
        let current = obj;
        for (let i = 0; i < keys.length; i++) {
            if (current === undefined || current === null || typeof current !== 'object' || !Object.prototype.hasOwnProperty.call(current, keys[i])) {
                return defaultValue;
            }
            current = current[keys[i]];
        }
        if (current === undefined || current === null) {
            return defaultValue;
        }
        if (Array.isArray(current)) {
            const filtered = current.filter(item => typeof item !== 'string' || !item.startsWith('$__META'));
            if (filtered.length > 0) {
                const actualValue = filtered[0];
                if (typeof actualValue === 'boolean' || typeof actualValue === 'number') {
                    return actualValue;
                }
                return String(actualValue);
            }
            return defaultValue;
        }
        if (typeof current === 'boolean' || typeof current === 'number' || typeof current === 'string') {
            return current;
        }
        return JSON.stringify(current);
    }

    function filterObjectArray(value) {
        if (!Array.isArray(value)) return [];
        return value.filter(item => item && typeof item === 'object' && !Array.isArray(item));
    }

    function formatNumber(value, fallback = '0') {
        const num = Number(value);
        if (Number.isFinite(num)) {
            return num.toLocaleString('zh-CN');
        }
        return fallback;
    }

    const statusCard = document.getElementById('status-card');
    const statusView = document.getElementById('status-view');
    const socialView = document.getElementById('social-view');
    const toggleBtn = document.getElementById('toggle-view-btn');
    const statusSubtitleElem = document.querySelector('.status-subtitle');

    const defaultSubtitle = statusSubtitleElem ? statusSubtitleElem.innerText : '夏川星来的日常';
    const socialSubtitle = 'Cosmix · 次元社区';
    const socialAccountName = '夏川星来Official';
    let cachedCharacterData = null;

    function renderStatus(characterData) {
        const timeElem = document.getElementById('current-time');
        if (timeElem) timeElem.innerText = SafeGetValue(characterData, '世界.当前时间');

        const locationElem = document.getElementById('current-location');
        if (locationElem) locationElem.innerText = SafeGetValue(characterData, '世界.当前地点');

        const seiraOutfit = document.getElementById('seira-outfit');
        if (seiraOutfit) seiraOutfit.innerText = SafeGetValue(characterData, '夏川星来.当前着装');

        const seiraPose = document.getElementById('seira-pose');
        if (seiraPose) seiraPose.innerText = SafeGetValue(characterData, '夏川星来.当前姿势');

        const seiraThoughts = document.getElementById('seira-thoughts');
        if (seiraThoughts) seiraThoughts.innerText = SafeGetValue(characterData, '夏川星来.当前想法');

        const cosStatusValue = SafeGetValue(characterData, 'COSPLAY.状态开关', false);
        const cosStatusElem = document.getElementById('cos-status');
        if (cosStatusElem) {
            const isActive = cosStatusValue === true || cosStatusValue === 'true';
            const badgeClass = isActive ? 'cos-badge-active' : 'cos-badge-inactive';
            const statusText = isActive ? 'ON' : 'OFF';
            cosStatusElem.innerHTML = `<span class="cos-badge ${badgeClass}">${statusText}</span>`;
        }

        const cosCharacter = document.getElementById('cos-character');
        if (cosCharacter) cosCharacter.innerText = SafeGetValue(characterData, 'COSPLAY.角色设定');

        const cosCostume = document.getElementById('cos-costume');
        if (cosCostume) cosCostume.innerText = SafeGetValue(characterData, 'COSPLAY.服装设定');
    }

    function renderSocial(characterData) {
        if (!socialView) return;

        const feedElem = document.getElementById('social-feed');
        if (!feedElem) return;

        const rawPosts = Array.isArray(characterData?.['社交动态']) ? characterData['社交动态'] : [];
        const posts = rawPosts.filter(item => item && typeof item === 'object' && !Array.isArray(item));

        feedElem.innerHTML = '';
        if (posts.length === 0) {
            feedElem.innerHTML = "<div class='empty-tip'>暂无动态</div>";
            return;
        }

        posts
            .slice()
            .reverse()
            .forEach(post => {
                const postElem = document.createElement('article');
                postElem.className = 'social-post';

                const header = document.createElement('div');
                header.className = 'social-post-header';

                const title = document.createElement('div');
                title.className = 'social-post-title';
                title.innerText = socialAccountName;

                const timestamp = document.createElement('time');
                timestamp.className = 'social-timestamp';
                timestamp.innerText = post?.['发布时间'] || '时间未知';

                header.appendChild(title);
                header.appendChild(timestamp);
                postElem.appendChild(header);

                const body = document.createElement('p');
                body.className = 'social-body';
                body.innerText = post?.['正文'] || '（暂无正文）';
                postElem.appendChild(body);

                const attachments = filterObjectArray(post?.['附件']);
                if (attachments.length > 0) {
                    const attachmentList = document.createElement('div');
                    attachmentList.className = 'attachment-list';

                    attachments.forEach(att => {
                        const item = document.createElement('div');
                        item.className = 'attachment-item';

                        const typeBadge = document.createElement('span');
                        typeBadge.className = 'attachment-type';
                        typeBadge.innerText = att?.['类型'] || '附件';

                        const desc = document.createElement('p');
                        desc.className = 'attachment-desc';
                        desc.innerText = att?.['描述'] || '（暂无描述）';

                        item.appendChild(typeBadge);
                        item.appendChild(desc);
                        attachmentList.appendChild(item);
                    });

                    postElem.appendChild(attachmentList);
                }

                const rewardCount = post?.['打赏人数'] || 0;
                const rewardTotal = post?.['打赏金额'] || 0;

                const metrics = document.createElement('div');
                metrics.className = 'social-metrics';

                const likeSpan = document.createElement('span');
                likeSpan.innerText = `👍 ${formatNumber(post?.['点赞数'], '0')}`;
                metrics.appendChild(likeSpan);

                const rewardSpan = document.createElement('span');
                rewardSpan.innerText = rewardCount > 0
                    ? `🎁 ${formatNumber(rewardCount)}人 · ¥${formatNumber(rewardTotal, '0')}`
                    : '🎁 暂无打赏';
                metrics.appendChild(rewardSpan);

                postElem.appendChild(metrics);

                const comments = filterObjectArray(post?.['评论列表']);
                if (comments.length > 0) {
                    const commentsWrap = document.createElement('div');
                    commentsWrap.className = 'social-comments';

                    const commentsTitle = document.createElement('div');
                    commentsTitle.className = 'comments-title';
                    commentsTitle.innerText = '热门评论';
                    commentsWrap.appendChild(commentsTitle);

                    comments.forEach(comment => {
                        const commentItem = document.createElement('div');
                        commentItem.className = 'comment-item';

                        const author = document.createElement('span');
                        author.className = 'comment-author';
                        author.innerText = comment?.['评论人'] || '匿名粉丝';

                        const content = document.createElement('p');
                        content.className = 'comment-content';
                        content.innerText = comment?.['内容'] || '（暂无内容）';

                        commentItem.appendChild(author);
                        commentItem.appendChild(content);
                        commentsWrap.appendChild(commentItem);
                    });

                    postElem.appendChild(commentsWrap);
                }

                feedElem.appendChild(postElem);
            });
    }

    function switchView(showSocial) {
        if (!statusCard || !statusView || !socialView || !toggleBtn) return;
        if (showSocial) {
            statusView.classList.add('view-hidden');
            socialView.classList.remove('view-hidden');
            statusCard.classList.add('social-mode');
            toggleBtn.textContent = '返回状态栏';
            toggleBtn.setAttribute('data-mode', 'social');
            if (statusSubtitleElem) statusSubtitleElem.innerText = socialSubtitle;
            if (cachedCharacterData) {
                renderSocial(cachedCharacterData);
            }
        } else {
            statusView.classList.remove('view-hidden');
            socialView.classList.add('view-hidden');
            statusCard.classList.remove('social-mode');
            toggleBtn.textContent = '查看动态';
            toggleBtn.setAttribute('data-mode', 'status');
            if (statusSubtitleElem) statusSubtitleElem.innerText = defaultSubtitle;
        }
    }

    switchView(false);

    if (toggleBtn) {
        toggleBtn.addEventListener('click', () => {
            const currentMode = toggleBtn.getAttribute('data-mode');
            switchView(currentMode !== 'social');
        });
    }

    async function initDisplay() {
        try {
            const messages = await getChatMessages(getCurrentMessageId());
            if (!messages || messages.length === 0 || !messages[0].data) {
                statusCard.innerHTML = "<div class='error-tip'>无法加载状态数据 (messages empty)</div>";
                return;
            }
            const gameData = messages[0].data;
            const characterData = gameData?.stat_data;
            if (!characterData) {
                statusCard.innerHTML = "<div class='error-tip'>无法加载状态数据 (characterData missing)</div>";
                return;
            }

            cachedCharacterData = characterData;
            renderStatus(characterData);
            renderSocial(characterData);

            if (toggleBtn && toggleBtn.getAttribute('data-mode') === 'social') {
                switchView(true);
            }
        } catch (error) {
            statusCard.innerHTML = `<div class='error-tip'>状态栏加载出错: ${error.message}</div>`;
        }
    }

    document.addEventListener('DOMContentLoaded', initDisplay);
</script>
</body>
</html>

