        .top-item {
            background: rgba(255, 255, 255, 0.95);
            border: 2px solid var(--border-color);
            border-radius: 8px;
            padding: 10px 12px;
            box-shadow: var(--shadow);
            backdrop-filter: blur(10px);
        }

        .top-label {
            font-size: 0.75em;
            color: var(--text-light);
            font-weight: 600;
            text-transform: uppercase;
            letter-spacing: 0.5px;
            margin-bottom: 4px;
            display: flex;
            align-items: center;
