        .progress-bar-value {
            height: 100%;
            background: linear-gradient(90deg, var(--primary-color), var(--secondary-color));
            border-radius: 6px;
            transition: width 0.4s ease;
            box-shadow: 0 0 10px rgba(244, 168, 154, 0.5);
        }

        /* 响应式调整 */
        @media (max-width: 480px) {
            body {
                padding: 6px;
            }

            .stat-item {
                padding: 10px;
            }

            .stat-label {
                font-size: 0.75em;
