    <!-- 角色状态区域 -->
    <div class="character-stats">
        <!-- 好感度 -->
        <div class="stat-item stat-affection">
            <div class="stat-label">💖 好感度</div>
            <div class="affection-display">
                <span class="affection-number" id="eyja-affection">0</span>
                <span class="affection-desc" id="eyja-affection-desc"></span>
            </div>
            <div class="progress-bar-container">
                <div id="eyja-affection-bar" class="progress-bar-value" style="width: 0%;"></div>
            </div>
        </div>

        <!-- 关系状态 -->
        <div class="stat-item">
            <div class="stat-label">👥 关系状态</div>
            <div class="stat-value" id="eyja-relationship">初识·新入职的员工与老板</div>
        </div>

        <!-- 当前着装 -->
        <div class="stat-item">
            <div class="stat-label">👗 当前着装</div>
            <div class="stat-value" id="eyja-outfit">经典的短款黑白女仆装</div>
        </div>

