<script>
    function SafeGetValue(obj, path, defaultValue = "N/A") {
        let keys = Array.isArray(path) ? path : path.split('.');
        let current = obj;
        for (let i = 0; i < keys.length; i++) {
            if (current === undefined || current === null || typeof current !== 'object' || !current.hasOwnProperty(keys[i])) {
                return defaultValue;
            }
            current = current[keys[i]];
        }
        if (current === undefined || current === null) {
            return defaultValue;
        }
        if (Array.isArray(current)) {
            if (current.length > 0) {
                const actualValue = current[0];
                if (typeof actualValue === 'boolean') {
                    return actualValue;
                }
                return String(actualValue);
            } else {
                return defaultValue;
            }
        }
        if (typeof current === 'boolean') {
            return current;
        }
        if (typeof current === 'number') {
            return current;
        }
        if (typeof current === 'string') {
            return current;
        }
        return JSON.stringify(current);
    }

    function updateProgressBar(barId, valueMainId, valueDescId, rawValue, min = 0, max = 100, color) {
        const progressBar = document.getElementById(barId);
        const valueMainDisplay = document.getElementById(valueMainId);
        const valueDescDisplay = document.getElementById(valueDescId);
        if (!progressBar || !valueMainDisplay) {
            return;
        }
        let numericValue = parseFloat(rawValue);
        if (isNaN(numericValue)) {
            numericValue = parseFloat(String(rawValue).replace(/[^0-9.-]/g, ''));
        }
        if (!isNaN(numericValue)) {
            valueMainDisplay.innerText = numericValue;
        } else {
            valueMainDisplay.innerText = rawValue;
        }
        if (valueDescDisplay) {
            valueDescDisplay.innerText = '';
        }
        if (!isNaN(numericValue)) {
            numericValue = Math.max(min, Math.min(max, numericValue));
            const range = max - min;
            const percentage = range === 0 ? (numericValue >= max ? 100 : 0) : ((numericValue - min) / range) * 100;
            progressBar.style.width = `${Math.max(0, Math.min(100, percentage))}%`;
            progressBar.style.backgroundColor = color ? color : '#ff6b9d';
        }
    }

    function toggleSection(sectionName) {
        const section = document.querySelector(`[data-section="${sectionName}"]`);
        if (section) {
            const content = section.querySelector('.section-content');
            const button = section.querySelector('.section-toggle');
            if (content.style.display === 'none' || !content.style.display) {
                content.style.display = 'block';
                if (button) button.textContent = '收起';
            } else {
                content.style.display = 'none';
                if (button) button.textContent = '展开';
            }
        }
    }

    async function initDisplay() {
        try {
            const messages = await getChatMessages(getCurrentMessageId());
            if (!messages || messages.length === 0 || !messages[0].data) {
                document.getElementById('status-card').innerHTML = "<div class='error-tip'>无法加载状态数据 (messages empty)</div>";
                return;
            }

            const gameData = messages[0].data;
            const characterData = gameData.stat_data;
            if (!characterData) {
                document.getElementById('status-card').innerHTML = "<div class='error-tip'>无法加载状态数据 (characterData missing)</div>";
                return;
            }

            const currentWorldElem = document.getElementById('current-world');
            if (currentWorldElem) currentWorldElem.innerText = SafeGetValue(characterData, '动画世界.当前世界名称');

            const explorationProgressElem = document.getElementById('exploration-progress');
            const explorationValue = SafeGetValue(characterData, '动画世界.探索度', 0);
            if (explorationProgressElem) explorationProgressElem.innerText = `${explorationValue}%`;

            updateProgressBar(
                'exploration-bar',
                'exploration-main',
                null,
                explorationValue,
                0, 100, '#ff6b9d'
            );

            const worldTypeElem = document.getElementById('world-type');
            if (worldTypeElem) worldTypeElem.innerText = SafeGetValue(characterData, '动画世界.世界类型');

            const mainPlotElem = document.getElementById('main-plot');
            if (mainPlotElem) mainPlotElem.innerText = SafeGetValue(characterData, '动画世界.重要剧情');

            const currentLocationElem = document.getElementById('current-location');
            if (currentLocationElem) currentLocationElem.innerText = SafeGetValue(characterData, '动画世界.当前地点');

            const currentTimeElem = document.getElementById('current-time');
            if (currentTimeElem) currentTimeElem.innerText = SafeGetValue(characterData, '动画世界.时间段');

            const hasActiveCharacter = SafeGetValue(characterData, '互动角色.是否存在');
            if (hasActiveCharacter === 'true' || hasActiveCharacter === true) {
                document.getElementById('tab-character').style.display = 'block';

                updateProgressBar(
                    'character-affection-bar',
                    'character-affection',
                    'character-affection-desc',
                    SafeGetValue(characterData, '互动角色.好感度'),
                    0, 100, '#a29bfe'
                );

                const characterNameElem = document.getElementById('character-name');
                if (characterNameElem) characterNameElem.innerText = SafeGetValue(characterData, '互动角色.姓名');

                const characterAgeElem = document.getElementById('character-age');
                if (characterAgeElem) characterAgeElem.innerText = SafeGetValue(characterData, '互动角色.年龄');

                const characterFigureElem = document.getElementById('character-figure');
                if (characterFigureElem) characterFigureElem.innerText = SafeGetValue(characterData, '互动角色.身材');

                const characterRelationshipElem = document.getElementById('character-relationship');
                if (characterRelationshipElem) characterRelationshipElem.innerText = SafeGetValue(characterData, '互动角色.关系状态');

                const characterOutfitElem = document.getElementById('character-outfit');
