                const characterThoughtsElem = document.getElementById('character-thoughts');
                if (characterThoughtsElem) characterThoughtsElem.innerText = SafeGetValue(characterData, '互动角色.当前想法');
            } else {
                document.getElementById('tab-character').style.display = 'none';
                document.getElementById('tab-content-character').classList.remove('active');
            }

            const activeTab = document.querySelector('.tab-button.active');
            if (activeTab && activeTab.style.display === 'none') {
                switchTab('main', document.querySelector('[data-tab="main"]'));
            }
        } catch (error) {
            document.getElementById('status-card').innerHTML = `<div class='error-tip'>状态栏加载出错: ${error.message}</div>`;
        }
    }

    function switchTab(tabName, tabElement) {
        const tabs = document.querySelectorAll('.tab-button');
        const contents = document.querySelectorAll('.tab-panel');
        tabs.forEach(tab => tab.classList.remove('active'));
        contents.forEach(content => content.classList.remove('active'));
        if (tabElement) {
            tabElement.classList.add('active');
        } else {
            const fallback = document.querySelector('[data-tab="' + tabName + '"]');
            if (fallback) {
                fallback.classList.add('active');
            }
        }
        const targetPanel = document.getElementById('tab-content-' + tabName);
        if (targetPanel) {
            targetPanel.classList.add('active');
        }
    }

    document.addEventListener('DOMContentLoaded', initDisplay);
</script>
